import React, { useEffect, useRef } from "react";
import Globe from "three-globe";
import * as THREE from "three";

function GlobeComponent({ logs }) {
  const globeRef = useRef();
  const globeInstance = useRef();

  useEffect(() => {
    if (!globeRef.current) return;

    // Filter valid logs
    const geoLogs = logs.filter((log) => log.country && log.city);

    // Group logs by country
    const countryData = geoLogs.reduce((acc, log) => {
      const country = log.country;
      if (!acc[country]) {
        acc[country] = {
          name: country,
          count: 0,
          lat: 0,
          lng: 0,
        };
      }
      acc[country].count += 1;
      const coords = getApproximateCoords(country, log.city);
      acc[country].lat = coords.lat;
      acc[country].lng = coords.lng;
      return acc;
    }, {});

    const countries = Object.values(countryData);

    // Create the globe instance
    const globe = new Globe()
      .globeImageUrl("//unpkg.com/three-globe/example/img/earth-night.jpg")
      .pointsData(countries)
      .pointLat("lat")
      .pointLng("lng")
      .pointColor(() => "#ff6b6b")
      .pointAltitude(0.01)
      .pointRadius((d) => Math.sqrt(d.count) * 0.5) // Scale radius by count
      .pointsMerge(true)
      .pointLabel(
        (d) => `
          <div style="background: rgba(0,0,0,0.8); color: white; padding: 5px; border-radius: 3px; font-size: 12px;">
            <b>${d.name}</b><br/>
            Requests: ${d.count}
          </div>
        `
      );

    // Set background color
    globe.backgroundColor("#000011");

    // Configure controls
    globe.controls().autoRotate = true;
    globe.controls().autoRotateSpeed = 0.5;
    globe.controls().enableZoom = true;

    // Add to DOM
    globeRef.current.appendChild(globe.renderer().domElement);
    globeInstance.current = globe;

    // Handle window resizing
    const handleResize = () => {
      const container = globeRef.current;
      if (container && globeInstance.current) {
        const { clientWidth, clientHeight } = container;
        globeInstance.current.width(clientWidth);
        globeInstance.current.height(clientHeight);
      }
    };

    window.addEventListener("resize", handleResize);
    handleResize();

    // Start animation loop
    const animate = () => {
      if (globeInstance.current) {
        globeInstance.current.renderer().render(
          globeInstance.current.scene(),
          globeInstance.current.camera()
        );
        requestAnimationFrame(animate);
      }
    };
    animate();

    // Cleanup on unmount
    return () => {
      window.removeEventListener("resize", handleResize);
      if (globeInstance.current && globeRef.current) {
        try {
          globeRef.current.removeChild(globeInstance.current.renderer().domElement);
        } catch (e) {
          // Element might already be removed
        }
        globeInstance.current = null;
      }
    };
  }, [logs]);

  return (
    <div
      ref={globeRef}
      style={{
        width: "100%",
        height: "100%",
        backgroundColor: "#000011",
        overflow: "hidden",
      }}
    />
  );
}

// üåç Helper: Approximate coordinates for countries
function getApproximateCoords(country, city) {
  const coords = {
    Nigeria: { lat: 9.082, lng: 8.6753 },
    "United States": { lat: 37.0902, lng: -95.7129 },
    "United Kingdom": { lat: 55.3781, lng: -3.436 },
    Germany: { lat: 51.1657, lng: 10.4515 },
    France: { lat: 46.2276, lng: 2.2137 },
    Japan: { lat: 36.2048, lng: 138.2529 },
    Australia: { lat: -25.2744, lng: 133.7751 },
    Canada: { lat: 56.1304, lng: -106.3468 },
    Brazil: { lat: -14.235, lng: -51.9253 },
    India: { lat: 20.5937, lng: 78.9629 },
    China: { lat: 35.8617, lng: 104.1954 },
    Russia: { lat: 61.524, lng: 105.3188 },
    "South Africa": { lat: -30.5595, lng: 22.9375 },
    Mexico: { lat: 23.6345, lng: -102.5528 },
    Argentina: { lat: -38.4161, lng: -63.6167 },
  };

  return (
    coords[country] || {
      lat: Math.random() * 180 - 90,
      lng: Math.random() * 360 - 180,
    }
  );
}

export default GlobeComponent;
